mapper.select:
SELECT abc.column_id AS abc_column_id, abc.category_code AS abc_category_code, abc.board_id AS abc_board_id, abc.min_num AS abc_min_num, abc.max_num AS abc_max_num, abc.name AS abc_name, abc.sequence AS abc_sequence, alv1.name AS abc_color, abc.object_version_number AS abc_object_version_number, ais.id AS ais_id, ais.name AS ais_name, ais.category_code AS ais_category_code, ais.is_completed AS ais_completed, ais.object_version_number AS ais_object_version_number, ai.issue_id AS ai_issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) AS ai_issue_num, ai.summary AS ai_summary, ai.assignee_id AS ai_assignee_id, alv.name AS ai_priority_name, ai.parent_issue_id AS ai_parent_issue_id, ai.type_code AS ai_type_code, ai.epic_id AS ai_epic_id, ai.object_version_number AS ai_object_version_number FROM agile_board_column abc LEFT JOIN agile_board_column_status_rel abcsr ON abc.column_id = abcsr.column_id LEFT JOIN agile_issue_status ais ON abcsr.status_id = ais.id LEFT JOIN (SELECT ai1.* from agile_issue ai1, agile_issue_sprint_rel aisr WHERE aisr.sprint_id = #{activeSprintId} AND ai1.issue_id = aisr.issue_id AND ai1.type_code != 'issue_epic'
AND ai1.type_code = 'story'
AND ai1.assignee_id = #{assigneeId}
AND ai1.issue_id IN (${filterSql})
) ai ON ai.status_id = ais.id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN agile_lookup_value alv ON ai.priority_code = alv.value_code LEFT JOIN agile_lookup_value alv1 ON abc.color_code = alv1.value_code WHERE abc.project_id = #{projectId} AND abc.board_id = #{boardId} ORDER BY abc.sequence

SELECT abc.column_id AS abc_column_id, abc.category_code AS abc_category_code, abc.board_id AS abc_board_id, abc.min_num AS abc_min_num, abc.max_num AS abc_max_num, abc.name AS abc_name, abc.sequence AS abc_sequence, alv1.name AS abc_color, abc.object_version_number AS abc_object_version_number, ais.id AS ais_id, ais.name AS ais_name, ais.category_code AS ais_category_code, ais.is_completed AS ais_completed, ais.object_version_number AS ais_object_version_number, ai.issue_id AS ai_issue_id, IF(ai.issue_num IS NULL, NULL, CONCAT_WS('-', api.project_code, ai.issue_num)) AS ai_issue_num, ai.summary AS ai_summary, ai.assignee_id AS ai_assignee_id, alv.name AS ai_priority_name, ai.parent_issue_id AS ai_parent_issue_id, ai.type_code AS ai_type_code, ai.object_version_number AS ai_object_version_number FROM agile_board_column abc LEFT JOIN agile_board_column_status_rel abcsr ON abc.column_id = abcsr.column_id LEFT JOIN agile_issue_status ais ON abcsr.status_id = ais.id LEFT JOIN (SELECT ai1.* from agile_issue ai1 WHERE ai1.issue_id IN
#{issueId}
) ai ON ai.status_id = ais.id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN agile_lookup_value alv ON ai.priority_code = alv.value_code LEFT JOIN agile_lookup_value alv1 ON abc.color_code = alv1.value_code WHERE abc.board_id = #{boardId} ORDER BY abc.sequence

mapper.update:
UPDATE agile_board_column
SET sequence              = sequence + 1,
    object_version_number = object_version_number + 1
WHERE board_id = #{boardId}
      AND sequence >= #{sequence} AND sequence < #{originSequence}

UPDATE agile_board_column
SET sequence              = sequence - 1,
    object_version_number = object_version_number + 1
WHERE board_id = #{boardId}
      AND sequence > #{originSequence} AND sequence <= #{sequence}

UPDATE agile_board_column
SET max_num               = #{columnInfo.maxNum},
    min_num               = #{columnInfo.minNum},
    object_version_number = object_version_number + 1
WHERE
    project_id = #{columnInfo.projectId}
    AND column_id = #{columnInfo.columnId}
    AND object_version_number = #{columnInfo.objectVersionNumber}
