mapper.select:
SELECT IFNULL(a.new_string,0) as new_value, d.issue_id, #{sprintDO.startDate} AS date, 'startSprint' AS type, 1 AS statistical, 0 AS old_value, CONCAT_WS( '-', api.project_code, d.issue_num ) AS issue_num, CONCAT_WS( '-', api2.project_code, cd.issue_num ) AS parent_issue_num, cd.issue_id AS parent_issue_id FROM ( SELECT ai.issue_id, ai.issue_num, ai.project_id, ai.parent_issue_id FROM agile_issue ai WHERE ai.issue_id IN
#{issueId}
) d LEFT JOIN ( select adl3.new_string,adl3.issue_id,adl3.creation_date from (SELECT MAX(adl.log_id) as log_id FROM agile_data_log adl WHERE adl.field = #{field} AND adl.creation_date < #{sprintDO.startDate} AND adl.issue_id IN
#{issueId}
GROUP BY adl.issue_id) acd left join agile_data_log adl3 on acd.log_id = adl3.log_id ) a ON a.issue_id = d.issue_id LEFT JOIN agile_project_info api ON api.project_id = d.project_id left JOIN agile_issue cd on d.parent_issue_id = cd.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = cd.project_id

select ifnull(bbb.new_value,0) AS new_value, bbb.issue_id as issue_id, bbb.date as date, 0 as old_value, 'addDuringSprint' as type, 1 as statistical, CONCAT_WS('-', api.project_code, aaa.issue_num) AS issue_num, CONCAT_WS('-', api2.project_code, ai2.issue_num) AS parent_issue_num, ai2.issue_id as parent_issue_id from ( SELECT adl.issue_id, adl.creation_date as date, (select adl2.new_value from agile_data_log adl2 where adl2.field = #{field} and adl2.issue_id = adl.issue_id and adl2.creation_date <= adl.creation_date order by adl2.creation_date limit 1) as new_value FROM agile_data_log adl WHERE adl.field = 'Sprint' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND FIND_IN_SET( #{sprintDO.sprintId}, adl.new_value ) AND adl.issue_id IN
#{issueId}
)bbb left join agile_issue aaa on aaa.issue_id = bbb.issue_id LEFT JOIN agile_issue ai2 on aaa.parent_issue_id = ai2.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = ai2.project_id LEFT JOIN agile_project_info api ON api.project_id = aaa.project_id

SELECT a.*, 0 as new_value, 'removeDuringSprint' as type, 1 as statistical, CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num, CONCAT_WS('-', api2.project_code, ai2.issue_num) AS parent_issue_num, ai2.issue_id as parent_issue_id FROM ( SELECT adl.issue_id, adl.creation_date as date, IFNULL((select adl2.new_value from agile_data_log adl2 where adl2.field = #{field} and adl2.issue_id = adl.issue_id and adl2.creation_date <= adl.creation_date order by adl2.creation_date limit 1) ,0)as old_value FROM agile_data_log adl WHERE adl.field = 'Sprint' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND FIND_IN_SET(#{sprintDO.sprintId},adl.old_value) AND (FIND_IN_SET(#{sprintDO.sprintId}, adl.new_value) IS NULL OR FIND_IN_SET(#{sprintDO.sprintId}, adl.new_value) = 0) AND adl.issue_id IN
#{issueId}
) a LEFT JOIN agile_issue ai on ai.issue_id = a.issue_id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN agile_issue ai2 on ai.parent_issue_id = ai2.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = ai2.project_id

SELECT ai.issue_id,abd.date,'addDuringSprint' as type,0 as old_value, 1 as new_value, 1 as statistical,CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num,CONCAT_WS('-', api2.project_code, ai2.issue_num) AS parent_issue_num, ai2.issue_id as parent_issue_id FROM agile_issue ai left join (SELECT adl.issue_id, adl.creation_date AS date FROM agile_data_log adl WHERE adl.field = 'Sprint' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND FIND_IN_SET(#{sprintDO.sprintId},adl.new_value) AND adl.issue_id IN
#{issueId} )abd on abd.issue_id = ai.issue_id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN agile_issue ai2 on ai.parent_issue_id = ai2.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = ai2.project_id where ai.issue_id in
#{issueId}

SELECT adl.issue_id FROM agile_data_log adl WHERE adl.field = 'resolution' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND adl.new_value != '' AND adl.issue_id in
#{issueId}
GROUP BY adl.issue_id

SELECT adl.issue_id FROM agile_data_log adl WHERE adl.field = 'resolution' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND adl.new_value is NULL and adl.issue_id in
#{issueId}
GROUP BY adl.issue_id

SELECT a.*, 0 as new_value, 'addDoneDuringSprint' as type, 1 as statistical, CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num, CONCAT_WS('-', api2.project_code, ai2.issue_num) AS parent_issue_num, ai2.issue_id as parent_issue_Id FROM ( SELECT adl.issue_id, adl.creation_date as date, IFNULL((select adl2.new_value from agile_data_log adl2 where adl2.field = #{field} and adl2.issue_id = adl.issue_id and adl2.creation_date >= adl.creation_date order by adl2.creation_date limit 1) ,0)as old_value FROM agile_data_log adl WHERE adl.field = 'resolution' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND adl.new_value IS not null AND adl.issue_id IN
#{issueId}
)a LEFT JOIN agile_issue ai on ai.issue_id = a.issue_id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN agile_issue ai2 on ai.parent_issue_id = ai2.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = ai2.project_id

SELECT a.*, 0 as old_value, 'removeDoneDuringSprint' as type, 1 as statistical, CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num, CONCAT_WS('-', api2.project_code, ai2.issue_num) AS parent_issue_num, ai2.issue_id as parent_issue_id FROM ( select adl.issue_id, adl.creation_date as date, IFNULL((select adl2.new_value from agile_data_log adl2 where adl2.field = #{field} and adl2.issue_id = adl.issue_id and adl2.creation_date >= adl.creation_date order by adl2.creation_date limit 1) ,0)as new_value FROM agile_data_log adl WHERE adl.field = 'resolution' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND adl.new_value IS null AND adl.issue_id IN
#{issueId}
) a LEFT JOIN agile_issue ai on ai.issue_id = a.issue_id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id LEFT JOIN agile_issue ai2 on ai.parent_issue_id = ai2.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = ai2.project_id

SELECT adl2.issue_id FROM ( SELECT MAX(adl.log_id) as log_id FROM agile_data_log adl WHERE adl.field = 'resolution' AND adl.creation_date < #{sprintDO.startDate} AND adl.issue_id IN
#{issueId}
GROUP BY adl.issue_id ) adl1 left join agile_data_log adl2 on adl2.log_id = adl1.log_id where adl2.new_value is not null

SELECT a.creation_date
FROM agile_issue_sprint_rel a
WHERE a.issue_id = #{issueId} AND a.sprint_id = #{sprintId}

SELECT
ai.issue_id
FROM agile_issue_sprint_rel aisr
LEFT JOIN (
SELECT adl.issue_id, adl.new_value, ais.is_completed
FROM agile_data_log adl, agile_issue_status ais, (
SELECT MAX(adl1.log_id) AS log_id
FROM agile_issue_sprint_rel aisr1, agile_data_log adl1, agile_issue_status ais1
WHERE aisr1.project_id = #{projectId} AND aisr1.sprint_id = #{sprintId}
AND aisr1.issue_id = adl1.issue_id AND adl1.field = 'status'
AND ais1.id = adl1.new_value AND ais1.is_completed IS NOT NULL
AND adl1.creation_date > #{startDate}

    AND adl1.creation_date < #{actualEndDate}

GROUP BY adl1.issue_id
) a
WHERE adl.log_id = a.log_id AND ais.id = adl.new_value
) adl2 ON adl2.issue_id = aisr.issue_id
LEFT JOIN (
SELECT adl.issue_id, adl.old_value, ais.is_completed
from agile_data_log adl, agile_issue_status ais, (
SELECT MIN(adl3.log_id) AS log_id
FROM agile_issue_sprint_rel aisr2, agile_data_log adl3
WHERE aisr2.project_id = #{projectId} AND aisr2.sprint_id = #{sprintId}
AND aisr2.issue_id = adl3.issue_id AND adl3.field = 'status'

    AND adl3.creation_date > #{actualEndDate}

GROUP BY adl3.issue_id
) a
WHERE adl.log_id = a.log_id AND ais.id = adl.old_value
) adl4 ON adl4.issue_id = aisr.issue_id,
agile_issue ai,agile_issue_status ais
WHERE aisr.project_id = #{projectId} AND aisr.sprint_id = #{sprintId}
AND aisr.issue_id = ai.issue_id AND ai.status_id = ais.id
AND ai.type_code != 'sub_task'
AND (adl2.new_value IS NOT null AND adl2.new_value != '' AND adl2.is_completed = ${status}
OR (
(adl2.new_value IS null OR adl2.new_value = '') AND adl4.old_value IS NOT null
AND adl4.old_value != '' AND adl4.is_completed = ${status}
)
OR (
(adl2.new_value IS null OR adl2.new_value = '') AND (adl4.old_value IS null OR adl4.old_value = '')
AND ais.is_completed = ${status}
)
)

SELECT adl.issue_id AS issue_id, adl.new_string AS story_points FROM agile_data_log adl, ( SELECT MAX(log_id) AS log_id FROM agile_data_log WHERE project_id = #{projectId} AND field = 'Story Points'
AND creation_date < #{actualEndDate}
AND issue_id IN
#{issueId}
GROUP BY issue_id ) a WHERE adl.log_id = a.log_id

SELECT adl.issue_id AS issue_id, ais.category_code AS category_code, ais.name AS status_name FROM agile_data_log adl, agile_issue_status ais, ( SELECT MAX(adl1.log_id) AS log_id FROM agile_data_log adl1, agile_issue_status ais1 WHERE adl1.project_id = #{projectId} AND adl1.field = 'status' AND ais1.id = adl1.new_value AND ais1.is_completed IS NOT NULL AND adl1.issue_id IN
#{issueId}
AND adl1.creation_date > #{startDate}
AND adl1.creation_date < #{actualEndDate}
GROUP BY adl1.issue_id ) a WHERE adl.log_id = a.log_id AND ais.id = adl.new_value

SELECT adl.issue_id AS issue_id, ais.category_code AS category_code, ais.name AS status_name FROM agile_data_log adl, agile_issue_status ais, ( SELECT MIN(log_id) AS log_id FROM agile_data_log WHERE project_id = #{projectId} AND field = 'status' AND issue_id IN
#{issueId}
AND creation_date > #{actualEndDate}
GROUP BY issue_id ) a WHERE adl.log_id = a.log_id AND ais.id = adl.old_value

SELECT IFNULL(adl2.new_string, 0) AS new_value
FROM (SELECT MAX(adl.log_id) AS log_Id
      FROM agile_data_log adl
      WHERE adl.issue_id = #{issueId} AND adl.field = #{field}
            AND adl.creation_date <= #{date}
      GROUP BY adl.issue_id) cd LEFT JOIN agile_data_log adl2 ON cd.log_id = adl2.log_id
