SELECT IFNULL(a.new_string,0) as new_value, d.issue_id, #{sprintDO.startDate} AS date, 'startSprint' AS type, 1 AS statistical, 0 AS old_value, CONCAT_WS( '-', api.project_code, d.issue_num ) AS issue_num, CONCAT_WS( '-', api2.project_code, cd.issue_num ) AS parent_issue_num, cd.issue_id AS parent_issue_id FROM ( SELECT ai.issue_id, ai.issue_num, ai.project_id, ai.parent_issue_id FROM agile_issue ai WHERE ai.issue_id IN
#{issueId}
) d LEFT JOIN ( select adl3.new_string,adl3.issue_id,adl3.creation_date from agile_data_log adl3 where adl3.log_id in (SELECT MAX(adl.log_id) as log_id FROM agile_data_log adl WHERE adl.field = #{field} AND adl.creation_date < #{sprintDO.startDate} AND adl.issue_id IN
#{issueId}
GROUP BY adl.issue_id) ) a ON a.issue_id = d.issue_id LEFT JOIN agile_project_info api ON api.project_id = d.project_id left JOIN agile_issue cd on d.parent_issue_id = cd.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = cd.project_id

select ifnull(bbb.new_value,0) AS new_value, bbb.issue_id as issue_id, bbb.date as date, 0 as old_value, 'addDuringSprint' as type, 1 as statistical, CONCAT_WS('-', api.project_code, aaa.issue_num) AS issue_num, CONCAT_WS('-', api2.project_code, ai2.issue_num) AS parent_issue_num, ai2.issue_id as parent_issue_id from ( SELECT adl.issue_id, adl.creation_date as date, (select adl2.new_string from agile_data_log adl2 where adl2.field = #{field} and adl2.issue_id = adl.issue_id and adl2.creation_date < adl.creation_date order by adl2.creation_date desc limit 1) as new_value FROM agile_data_log adl WHERE adl.field = 'Sprint' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND FIND_IN_SET( #{sprintDO.sprintId}, adl.new_value ) AND adl.issue_id IN
#{issueId}
)bbb left join agile_issue aaa on aaa.issue_id = bbb.issue_id LEFT JOIN agile_issue ai2 on aaa.parent_issue_id = ai2.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = ai2.project_id LEFT JOIN agile_project_info api ON api.project_id = aaa.project_id

