SELECT a.*, 0 as old_value, 'removeDoneDuringSprint' as type, 1 as statistical, CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num, CONCAT_WS('-', api2.project_code, ai2.issue_num) AS parent_issue_num, ai2.issue_id as parent_issue_id FROM ( select adl.issue_id, adl.creation_date as date, IFNULL((select adl2.new_string from agile_data_log adl2 where adl2.field = #{field} and adl2.issue_id = adl.issue_id and adl2.creation_date < adl.creation_date order by adl2.creation_date desc limit 1) ,0)as new_value FROM agile_data_log adl WHERE adl.field = 'resolution' AND adl.creation_date > #{sprintDO.startDate} AND adl.creation_date <= #{sprintDO.actualEndDate} AND adl.new_value IS null AND adl.issue_id IN
