SELECT abb.*, ( SELECT abcsr.column_id FROM agile_board_column_status_rel abcsr WHERE abcsr.status_id = abb.status_to AND abcsr.column_id IN
#{columnId}
) AS column_to, 0 AS column_from FROM ( SELECT ai.issue_id, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ai.creation_date AS date, ( SELECT adl.old_value FROM agile_data_log adl WHERE adl.field = 'status' AND adl.issue_id = ai.issue_id ORDER BY adl.creation_date ASC LIMIT 1 ) AS status_to FROM agile_issue ai LEFT JOIN agile_project_info api ON api.project_id = ai.project_id WHERE ai.issue_id IN
#{issueId}
AND ai.creation_date >= #{startDate} AND ai.creation_date <= #{endDate} ) abb

SELECT a.* from (SELECT adl.issue_id, CONCAT_WS( '-', api.project_code, ai.issue_num ) AS issue_num, ifnull(abcsr.column_id,0) AS column_from, ifnull(abcsr2.column_id,0) AS column_to, adl.new_value AS status_to, adl.creation_date AS date FROM agile_data_log adl LEFT JOIN agile_board_column_status_rel abcsr ON abcsr.status_id = adl.old_value and abcsr.column_id in
#{columnId}
LEFT JOIN agile_board_column_status_rel abcsr2 ON abcsr2.status_id = adl.new_value and abcsr2.column_id in
#{columnId}
LEFT JOIN agile_issue ai ON ai.issue_id = adl.issue_id LEFT JOIN agile_project_info api ON api.project_id = ai.project_id WHERE adl.field = 'status' AND adl.issue_id IN
#{issueId}
AND adl.creation_date >= #{startDate} AND adl.creation_date <= #{endDate})a WHERE a.column_from != a.column_to and a.column_to!=0

SELECT
    adl.creation_date AS change_date,
    a.issue_id        AS add_issue_id,
    b.issue_id        AS remove_issue_id,
    NULL              AS issue_id,
    adl.log_id        AS log_id
FROM agile_data_log adl
    LEFT JOIN (SELECT
                   creation_date,
                   issue_id
               FROM agile_data_log
               WHERE project_id = #{projectId}
                     AND field = 'Fix Version'
                     AND creation_date >= #{startDate}
                     AND creation_date <= #{endDate}
                     AND new_value = #{versionId}
              ) a ON a.creation_date = adl.creation_date
    LEFT JOIN (SELECT
                   creation_date,
                   issue_id
               FROM agile_data_log
               WHERE project_id = #{projectId}
                     AND field = 'Fix Version'
                     AND creation_date >= #{startDate}
                     AND creation_date <= #{endDate}
                     AND old_value = #{versionId}
              ) b ON b.creation_date = adl.creation_date
    ,
    agile_issue ai
WHERE adl.project_id = #{projectId}
      AND adl.issue_id = ai.issue_id
      AND ai.type_code IN ('task', 'bug', 'issue_epic', 'story')
      AND adl.field = 'Fix Version'
      AND adl.creation_date >= #{startDate}
      AND adl.creation_date <= #{endDate}
      AND (adl.new_value = #{versionId} OR adl.old_value = #{versionId})
ORDER BY adl.creation_date DESC

SELECT adl.issue_id, CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num,
adl.old_string AS old_value, adl.new_string AS new_value,
adl.creation_date AS change_date, IFNULL(adl.new_string, 0) - IFNULL(adl.old_string, 0) AS change_field,
ai.type_code AS type_code, IFNULL(b.completed, FALSE) AS completed
FROM agile_data_log adl
LEFT JOIN agile_issue ai ON ai.issue_id = adl.issue_id
LEFT JOIN agile_project_info api ON ai.project_id = api.project_id
LEFT JOIN (
SELECT adl1.issue_id, ais.is_completed AS completed,
a.change_date AS change_date
FROM agile_data_log adl1
LEFT JOIN agile_issue_status ais ON ais.id = adl1.new_value,
(

    SELECT MAX(log_id) AS log_id, #{versionIssue.changeDate} AS change_date
    FROM agile_data_log
    WHERE project_id = #{projectId} AND field = 'resolution'
    AND creation_date <= #{versionIssue.changeDate}
    AND issue_id IN

        #{issueId}

    GROUP BY issue_id

)a
WHERE adl1.log_id = a.log_id
) b ON b.change_date = adl.creation_date AND b.issue_id= adl.issue_id
WHERE adl.project_id = #{projectId} AND adl.field = #{field}
AND (

    ( adl.creation_date < #{versionIssue.changeDate} AND adl.creation_date >= #{versionIssue.preDate}
    AND adl.issue_id IN

        #{issueId}

    )

)

SELECT adl.issue_id, adl.creation_date AS change_date, NULL AS add_issue_id, NULL AS remove_issue_id, adl.log_id AS log_id FROM agile_data_log adl LEFT JOIN agile_issue_status ais ON
ais.id = adl.new_value
ais.id = adl.old_value
WHERE adl.project_id = #{projectId} AND adl.field = 'resolution' AND ais.is_completed = TRUE AND (
( adl.creation_date < #{versionIssue.changeDate} AND adl.creation_date >= #{versionIssue.preDate} AND adl.issue_id IN
#{issueId}
)
)

SELECT adl.issue_id, CONCAT_WS('-', api.project_code, ai.issue_num) AS issue_num,
b.old_value AS old_value, b.new_value AS new_value,
adl.creation_date AS change_date, ai.type_code AS type_code,
IFNULL(b1.completed, FALSE) AS completed
FROM agile_data_log adl
LEFT JOIN agile_issue ai ON ai.issue_id = adl.issue_id
LEFT JOIN agile_project_info api ON ai.project_id = api.project_id
LEFT JOIN (
SELECT adl1.issue_id, adl1.old_string AS old_value,
adl1.new_string AS new_value, a.change_date AS change_date
FROM agile_data_log adl1,
(

    SELECT MAX(log_id) AS log_id, #{changeIssue.changeDate} AS change_date
    FROM agile_data_log
    WHERE project_id = #{projectId} AND field = #{field}
    AND creation_date <= #{changeIssue.changeDate}
    AND issue_id IN

        #{issueId}

    GROUP BY issue_id

)a
WHERE adl1.log_id = a.log_id
) b ON b.change_date = adl.creation_date AND b.issue_id= adl.issue_id
LEFT JOIN (
SELECT adl1.issue_id, ais.is_completed AS completed,
a.change_date AS change_date
FROM agile_data_log adl1
LEFT JOIN agile_issue_status ais ON ais.id = adl1.new_value,
(

    SELECT MAX(log_id) AS log_id, #{changeIssue.changeDate} AS change_date
    FROM agile_data_log
    WHERE project_id = #{projectId} AND field = 'resolution'
    AND creation_date <= #{changeIssue.changeDate}
    AND issue_id IN

        #{issueId}

    GROUP BY issue_id

)a
WHERE adl1.log_id = a.log_id
) b1 ON b1.change_date = adl.creation_date AND b1.issue_id= adl.issue_id
WHERE adl.project_id = #{projectId}
AND (

    ( adl.creation_date = #{changeIssue.changeDate}
    AND adl.log_id IN

        #{logId}

    AND adl.issue_id IN

        #{issueId}

    )

)

