other.${fieldName} AS type_name, otherName.name as name,
other.${fieldName} AS type_name, otherName.sprint_name as name,
count( * ) AS value FROM agile_issue ai
LEFT JOIN agile_lookup_value alv on ai.${fieldName} = alv.value_code
LEFT JOIN agile_issue_status ais on ais.id = ai.${fieldName}
LEFT JOIN agile_issue ai2 on ai2.issue_id = ai.${fieldName}
LEFT JOIN agile_issue_status other on ai.status_id = other.id
LEFT JOIN agile_component_issue_rel other on ai.issue_id = other.issue_id LEFT JOIN agile_issue_component otherName on other.${fieldName} = otherName.${fieldName}
LEFT JOIN agile_version_issue_rel other on ai.issue_id = other.issue_id and other.relation_type = 'fix' LEFT JOIN agile_product_version otherName on other.${fieldName} = otherName.${fieldName}
LEFT JOIN agile_issue_sprint_rel other on ai.issue_id = other.issue_id LEFT JOIN agile_sprint otherName on other.${fieldName} = otherName.${fieldName}
WHERE ai.project_id = #{projectId} AND ai.type_code != "issue_test"
AND ai.type_code != "issue_epic"
GROUP BY
ai.${fieldName}
other.is_completed
other.${fieldName}
)pieData ORDER BY pieData.value DESC
