mapper.select:
SELECT asp.sprint_id AS id, IFNULL(ic.issue_count, 0) AS issue_count FROM agile_sprint asp LEFT JOIN ( SELECT ai.sprint_id AS sprint_id, COUNT(1) AS issue_count FROM agile_issue ai, agile_issue_status ais WHERE ai.project_id = #{projectId} AND ai.type_code NOT IN ('sub_task', 'issue_epic') AND ai.status_id = ais.id AND ais.category_code != 'done' AND ai.sprint_id IN
#{sprintId}
GROUP BY ai.sprint_id ) ic ON ic.sprint_id = asp.sprint_id WHERE asp.project_id = #{projectId} AND asp.sprint_id IN
#{sprintId}

SELECT IFNULL(SUM(ai.story_points), 0)
FROM agile_issue ai, agile_issue_status ais
WHERE ai.project_id = #{projectId} AND ai.sprint_id = #{sprintId}
AND ai.type_code = 'story' AND ai.status_id = ais.id AND ais.category_code = #{categoryCode}


        AND ai.assignee_id = #{userId}

SELECT *
FROM agile_sprint
WHERE sprint_id = #{sprintId} AND project_Id = #{projectId}

SELECT IF(COUNT(1) = 0, FALSE, TRUE) FROM agile_issue WHERE project_id = #{projectId} AND type_code NOT IN ('sub_task', 'issue_epic')
AND sprint_id = #{sprintId}
AND (sprint_id IS NULL OR sprint_id = 0)

SELECT MAX(rank) FROM agile_issue WHERE project_id = #{projectId} AND type_code NOT IN ('sub_task', 'issue_epic')
AND sprint_id = #{sprintId}
AND (sprint_id IS NULL OR sprint_id = 0)

SELECT MIN(rank) FROM agile_issue WHERE project_id = #{projectId} AND type_code NOT IN ('sub_task', 'issue_epic')
AND sprint_id = #{sprintId}
AND (sprint_id IS NULL OR sprint_id = 0)

SELECT ai.sprint_id, ai.assignee_id, SUM(ai.story_points) AS total_story_points, SUM(ai.remaining_time) AS total_remaining_time, COUNT(1) AS issue_count FROM agile_issue ai, agile_issue_status ais WHERE ai.project_id = #{projectId} AND ai.type_code NOT IN ('sub_task', 'issue_epic') AND ai.status_id = ais.id AND ais.category_code != 'done' AND ai.sprint_id IN
#{sprintId}
AND ai.issue_id IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id = #{advancedSearchArgs.versionId})
AND ai.epic_id = #{advancedSearchArgs.epicId}
AND ai.issue_id NOT IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id != 0)
AND (ai.epic_id IS NULL OR ai.epic_id = 0)
AND ai.assignee_id = #{userId}
AND ai.type_code = 'story'
GROUP BY ai.sprint_id, ai.assignee_id

SELECT sprint_name
FROM agile_sprint
WHERE sprint_id = #{sprintId}
