mapper.select:
SELECT asp.sprint_id AS id, IFNULL(ic.issue_count, 0) AS issue_count FROM agile_sprint asp LEFT JOIN ( SELECT aisr.sprint_id AS sprint_id, COUNT(1) AS issue_count FROM agile_issue ai, agile_issue_status ais, agile_issue_sprint_rel aisr WHERE ai.project_id = #{projectId} AND aisr.issue_id = ai.issue_id AND ai.type_code NOT IN ('sub_task', 'issue_epic') AND ai.status_id = ais.id AND ais.category_code != 'done' AND aisr.sprint_id IN
#{sprintId}
GROUP BY aisr.sprint_id ) ic ON ic.sprint_id = asp.sprint_id WHERE asp.project_id = #{projectId} AND asp.sprint_id IN
#{sprintId}

SELECT IFNULL(SUM(ai.story_points), 0)
FROM agile_issue ai, agile_issue_status ais, agile_issue_sprint_rel aisr
WHERE ai.project_id = #{projectId} AND aisr.issue_id = ai.issue_id AND aisr.sprint_id = #{sprintId}
AND ai.type_code = 'story' AND ai.status_id = ais.id AND ais.category_code = #{categoryCode}


        AND ai.assignee_id = #{userId}



    AND ai.issue_id IN (${filterSql})

SELECT sprint_id, sprint_name FROM agile_sprint WHERE project_id = #{projectId}
AND status_code IN
#{sprintStatusCode}
ORDER BY last_update_date DESC

SELECT *
FROM agile_sprint
WHERE sprint_id = #{sprintId} AND project_Id = #{projectId}

SELECT IF(COUNT(1) = 0, FALSE, TRUE) FROM agile_issue ai LEFT JOIN agile_issue_sprint_rel aisr ON aisr.issue_id = ai.issue_id WHERE ai.project_id = #{projectId} AND ai.type_code NOT IN ('sub_task', 'issue_epic')
AND aisr.sprint_id = #{sprintId}
AND (aisr.sprint_id IS NULL OR aisr.sprint_id = 0)

SELECT MAX(ai.rank) FROM agile_issue ai LEFT JOIN (SELECT aisr1.sprint_id, aisr1.issue_id FROM agile_issue_sprint_rel aisr1, agile_sprint asp WHERE aisr1.project_id = #{projectId} AND aisr1.sprint_id = asp.sprint_id AND asp.status_code != 'closed') aisr ON aisr.issue_id = ai.issue_id WHERE ai.project_id = #{projectId} AND ai.type_code NOT IN ('sub_task', 'issue_epic')
AND aisr.sprint_id = #{sprintId}
AND (aisr.sprint_id IS NULL OR aisr.sprint_id = 0)

SELECT MIN(ai.rank) FROM agile_issue ai LEFT JOIN (SELECT aisr1.sprint_id, aisr1.issue_id FROM agile_issue_sprint_rel aisr1, agile_sprint asp WHERE aisr1.project_id = #{projectId} AND aisr1.sprint_id = asp.sprint_id AND asp.status_code != 'closed') aisr ON aisr.issue_id = ai.issue_id WHERE ai.project_id = #{projectId} AND ai.type_code NOT IN ('sub_task', 'issue_epic')
AND aisr.sprint_id = #{sprintId}
AND (aisr.sprint_id IS NULL OR aisr.sprint_id = 0)

SELECT aisr.sprint_id, ai.assignee_id, SUM(ai.story_points) AS total_story_points, SUM(ai.remaining_time) AS total_remaining_time, COUNT(1) AS issue_count FROM agile_issue ai, agile_issue_status ais, agile_issue_sprint_rel aisr WHERE ai.project_id = #{projectId} AND aisr.issue_id = ai.issue_id AND ai.type_code NOT IN ('sub_task', 'issue_epic') AND ai.status_id = ais.id AND ais.category_code != 'done' AND aisr.sprint_id IN
#{sprintId}
AND ai.issue_id IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id = #{advancedSearchArgs.versionId})
AND ai.epic_id = #{advancedSearchArgs.epicId}
AND ai.issue_id NOT IN (SELECT issue_id FROM agile_version_issue_rel WHERE version_id != 0)
AND (ai.epic_id IS NULL OR ai.epic_id = 0)
AND ai.assignee_id = #{userId}
AND ai.type_code = 'story'
AND ai.issue_id IN (${filterSql})
GROUP BY aisr.sprint_id, ai.assignee_id

SELECT sprint_id, sprint_name
FROM agile_sprint
WHERE project_id = #{projectId} AND sprint_id = #{sprintId}
